    const [name,setName]=useState('');
    const [age,setAge]=useState('');
    const Is_valid=useState(true)

    const nameHandler=(event)=>{
        setName(event.target.value)
        console.log(name)

    }
    const ageHandler=(event)=>{
        setAge(event.target.value)
        console.log(age)
    }
    const addSubmitHandler=(e)=>{
        e.preventDefault()
        if(name.trim().length===0 || age.trim().lenght===0){
           return

        }
        if(+age<0){
            return
        }
        props.addingHandler(name,age)
        setName('')
        setAge('')




    }




    return (
        <div>
        <Error></Error>
