export default (props)=>{
    const [name,setName]=useState('');
    const [age,setAge]=useState('');
    const Is_valid=useState(true)

    const nameHandler=(event)=>{
        setName(event.target.value)
        console.log(name)

    }
    const ageHandler=(event)=>{
        setAge(event.target.value)
        console.log(age)
    }
    const addSubmitHandler=(e)=>{
        e.preventDefault()
        if(name.trim().length===0 || age.trim().lenght===0){
           return

        }
        if(+age<0){
            return
        }
        props.addingHandler(name,age)




    }




    return (
        <Card className={Classes.input}>
            <form onSubmit={addSubmitHandler}>
                <label htmlFor='name'>Name:</label>
                <input type="text" id="name" onChange={nameHandler}></input>
                <label htmlFor='Age'>Age:</label>
                <input type="text" id="age" onChange={ageHandler}></input>
                <Button type={'onSubmit'} title={'Add User'}></Button>
            </form>
        </Card>

    );

}
